import { ActionTypes } from "../constants/actions-type";

const initialState = {
    products: []
}

export const productReducer = (state= initialState, { type, payload }) => {
    switch (type) {
        case ActionTypes.SET_PRODUCTS:
            return state;
        default:
            return state;
    }
}
